const searchLink=document.querySelector(".button-popup"),searchPopup=document.querySelector(".form-popup"),searchForm=searchPopup.querySelector(".search-form"),searchDateArrival=searchPopup.querySelector(".date-arrival-input"),searchDateDeparture=searchPopup.querySelector(".date-of-departure-input");searchLink.addEventListener("click",function(e){e.preventDefault(),searchPopup.classList.toggle("search-show"),searchDateArrival.focus(),searchPopup.classList.remove("search-error")}),searchForm.addEventListener("submit",function(e){searchDateArrival.value&&searchDateDeparture.value||(e.preventDefault(),searchPopup.classList.remove("search-error"),searchPopup.offsetWidth=searchPopup.offsetWidth,searchPopup.classList.add("search-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&searchPopup.classList.contains("search-show")&&(e.preventDefault(),searchPopup.classList.remove("search-show"),searchPopup.classList.remove("search-error"))});
